DepsInstall()
{
    if [ "$dist" == "rpm" ] 
    then
        if [[ -x $(command -v getenforce) ]] && [ "$(getenforce)" != "Disabled" ]
        then
            setenforce 0
        fi

        depends=(wget unzip vim curl crond logrotate patch nscd)

        if [[ ! -x $(command -v dig) ]] 
        then
            depends+=(bind-utils)
        fi

        if [[ ! -x $(command -v hexdump) ]] 
        then
            depends+=(util-linux)
        fi

        if [[ ! -x $(command -v ss) ]] 
        then
            depends+=(iproute)
        fi

        for depend in "${depends[@]}"
        do
            if [[ ! -x $(command -v "$depend") ]] 
            then
                if yum -y install "$depend" >/dev/null 2>&1
                then
                    Println "`eval_gettext \"\\\$info 依赖 \\\$depend 安装成功\"`"
                else
                    Println "`eval_gettext \"\\\$error 依赖 \\\$depend 安装失败\"`\n"
                    exit 1
                fi
            fi
        done

        yum -y update ca-certificates >/dev/null 2>&1 || yum -y reinstall ca-certificates >/dev/null 2>&1
    else
        if [ "$dist" == "deb" ] 
        then
            DebFixSources
        fi

        AptUpdate

        apt-get -y install ca-certificates >/dev/null 2>&1

        depends=(wget unzip vim curl cron ufw python3 logrotate patch nscd)

        if [[ ! -x $(command -v dig) ]] 
        then
            depends+=(dnsutils)
        fi

        if [[ ! -x $(command -v hexdump) ]] 
        then
            depends+=(bsdmainutils)
        fi

        for depend in "${depends[@]}"
        do
            if [[ ! -x $(command -v "$depend") ]] 
            then
                if apt-get -y install "$depend" >/dev/null 2>&1
                then
                    Println "`eval_gettext \"\\\$info 依赖 \\\$depend 安装成功\"`"
                else
                    Println "`eval_gettext \"\\\$error 依赖 \\\$depend 安装失败\"`\n"
                    exit 1
                fi
            fi
        done
    fi
}

# based on https://raw.githubusercontent.com/tanhauhau/Inquirer.sh/master/dist/inquirer.sh
inquirer()
{
    if [[ ! -x $(command -v tput) ]] 
    then
        DepInstall tput
    fi

    inquirer:print() {
        tput el
        printf '%b' "$1"
    }

    inquirer:print_input() {
        tput el
        printf '%s' "$1"
    }

    inquirer:join() {
        local var=("$1"[@])
        if [[ -z ${!var:-} ]] 
        then
            return
        fi
        local join_list=("${!var}") first=true item
        for item in "${join_list[@]}"
        do
            if [ "$first" = true ]
            then
                printf '%b' "$item"
                first=false
            else
                printf "${2-, }%b" "$item"
            fi
        done
    }

    inquirer:on_default() {
        return
    }

    inquirer:on_keypress() {
        local oIFS=$IFS
        local key char_read byte_len
        local oLC_ALL=${LC_ALL:-} oLANG=${LANG:-}
        local on_up=${1:-inquirer:on_default}
        local on_down=${2:-inquirer:on_default}
        local on_space=${3:-inquirer:on_default}
        local on_enter=${4:-inquirer:on_default}
        local on_left=${5:-inquirer:on_default}
        local on_right=${6:-inquirer:on_default}
        local on_ascii=${7:-inquirer:on_default}
        local on_backspace=${8:-inquirer:on_default}
        local on_not_ascii=${9:-inquirer:on_default}
        break_keypress=false

        while IFS= read -rsn1 key
        do
            case "$key" in
                $'\x1b')
                    read -rsn1 key
                    if [ "$key" == "[" ]
                    then
                        read -rsn1 key
                        case "$key" in
                        'A') $on_up;;
                        'B') $on_down;;
                        'D') $on_left;;
                        'C') $on_right;;
                        esac
                    fi
                ;;
                $'\x20') $on_space;;
                $'\x7f') $on_backspace "$key";;
                '') $on_enter "$key";;
                # The space is the first printable character listed on http://www.asciitable.com/, ~ is the last
                # [^ -~]
                *[$'\x80'-$'\xFF']*) 
                    if [[ ${BASH_VERSINFO[0]} -lt 4 ]] 
                    then
                        char_read="${char_read:-}$key"
                        LC_ALL= LANG=C
                        byte_len=${#char_read}
                        LC_ALL=$oLC_ALL LANG=$oLANG
                        if [ "$byte_len" -ne "${#char_read}" ] 
                        then
                            $on_not_ascii "$char_read"
                            char_read=""
                        fi
                    else
                        $on_not_ascii "$key"
                    fi
                ;;
                $'\x09')
                    local i
                    for((i=0;i<4;i++));
                    do
                        $on_space
                    done
                ;;
                *) $on_ascii "$key";;
            esac
            if [ "$break_keypress" = true ]
            then
                break
            fi
        done

        IFS="$oIFS"
    }

    inquirer:cleanup() {
        tput sgr0
        tput cnorm
        stty echo
    }

    inquirer:control_c() {
        inquirer:cleanup
        exit $?
    }

    inquirer:remove_instructions() {
