                    jq_commands+=( '$value)' )
                else
                    jq_commands+=( '($value|tonumber))' )
                fi

                if [ -n "$jq_path2" ] 
                then
                    jq_commands+=( '|getpath($path2))' )
                fi

                jq_args+=( --arg key "$3" --arg value "$4" )

                if [ "$file" = true ] 
                then
                    slurp_arg=("$5"[@])
                else
                    jq_args+=( --argjson add "$5" )
                fi

                if [ "$pre" = true ] 
                then
                    jq_commands+=( '|=$add+.)' )
                else
                    jq_commands+=( '+=$add)' )
                fi
            else
                if [ "$file" = true ] 
