            stream_link="https${stream_link:4}"
        fi

        user_agent="${user_agent:-$USER_AGENT_BROWSER}"
        headers="${headers:-Referer: $stream_link\\r\\n}"
        cookies="${cookies:-}"

        while IFS= read -r line 
        do
            if [[ $line =~ tag_deviceid= ]] 
            then
                line=${line#* }
                cookies=${line%% *}
            elif [[ $line =~ country_code= ]] 
