                                end=${chnl_stream_url_num#*-}
                                for((i=start-1;i<end;i++));
                                do
                                    chnl_stream_url_qualities+=("${chnl_stream_urls_bitrate[i]}-${chnl_stream_urls_resolution[i]}")
                                    chnl_stream_url_video_indices+=("$i")
                                done
