                JQs add parse_out "[${!new_directive}]"
            done

            if [ -n "$new_directive" ] 
            then
                NginxGetConfig
                updated=1
            fi
        ;;
        4) 
            local level_1_index level_2_index level_3_index level_4_index
            new_directive=""
            add_count=0
            level_4_add_indices=()

            for((directive_i=0;directive_i<${#directives[@]};directive_i++));
