        | sed "s+https://github.com/v2fly/v2ray-core/releases/download+$FFMPEG_MIRROR_LINK/$v2ray_name+g" | bash
    else
        { curl -s -m 10 "$V2_LINK" || curl -s -m 30 "$V2_LINK_FALLBACK"; } \
        | sed "s+nobody+$v2ray_name+g" \
        | sed "s+ 'sha1'++g" \
        | sed "s+ 'sha256'++g" \
        | sed "s+ 'sha512'++g" \
