V2rayConfigUpdate()
{
    if [ ! -e "$V2_CONFIG" ] 
    then
        Println "$error $v2ray_name 未安装...\n"
        exit 1
    fi

    if ! outbounds=$($JQ_FILE '.outbounds' "$V2_CONFIG" 2> /dev/null) || [ "$outbounds" == "null" ]
    then
        if grep -q '"path": "' < "$V2_CONFIG" 
        then
            while IFS= read -r line 
            do
                if [[ $line == *"path"* ]] 
                then
                    path=${line#*: \"}
                    path=${path%\"*}
                    break
                fi
            done < "$V2_CONFIG"
        fi

        printf -v update_date '%(%m-%d-%H:%M:%S)T' -1
        cp -f "$V2_CONFIG" "${V2_CONFIG}_$update_date"
        while IFS= read -r line 
        do
            if [[ $line == *"port"* ]] 
            then
                port=${line#*: }
                port=${port%,*}
            elif [[ $line == *"id"* ]] 
            then
                id=${line#*: \"}
                id=${id%\"*}
                break
            fi
        done < "$V2_CONFIG"

        $JQ_FILE -n --arg port "${port:-$(GetFreePort)}" --arg id "${id:-$($V2CTL_FILE uuid)}" --arg path "${path:-/$(RandStr)}" \
        --arg error "/var/log/$v2ray_name/error.log" \
        '{
            "log": {
                "access": "none",
                "error": $error,
                "loglevel": "error"
            },
            "inbounds": [
                {
                    "listen": "127.0.0.1",
                    "port": $port | tonumber,
                    "protocol": "vmess",
                    "settings": {
                        "clients": [
                            {
                                "id": $id,
                                "level": 0,
                                "alterId": 64,
                                "email": "name@localhost"
                            }
                        ]
                    },
                    "streamSettings": {
                        "network": "ws",
                        "wsSettings": {
                            "path": $path
                        }
                    },
                    "tag": "nginx-1"
                }
            ],
            "outbounds": [
                {
                    "protocol": "freedom",
                    "tag": "direct"
                },
                {
                    "protocol": "blackhole",
                    "tag": "block"
                }
            ],
            "policy": {
                "levels": {
                    "0": {
                        "handshake": 4,
                        "connIdle": 300,
                        "uplinkOnly": 2,
                        "downlinkOnly": 5,
                        "statsUserUplink": false,
                        "statsUserDownlink": false,
                        "bufferSize": 512
                    }
                },
                "system": {
                    "statsInboundUplink": false,
                    "statsInboundDownlink": false,
                    "statsOutboundUplink": false,
                    "statsOutboundDownlink": false
                }
            }
        }' > "$V2_CONFIG"

        Println "$info $v2ray_name 配置文件已更新\n"
    fi
}

