        cf_hosts_zone_always_use_https+=("$zone_always_use_https")
        cf_hosts_zone_ssl+=("$zone_ssl")
        zone_subdomains=${zone_subdomains%\"}
        cf_hosts_zone_subdomains+=("$zone_subdomains")

