IbmLoginUser()
{
    IbmListUsers

    if [ "$ibm_users_count" -eq 0 ] 
    then
        Println "$error 请先添加用户\n"
        exit 1
    fi

    echo -e "选择用户"
    while read -p "$i18n_default_cancel" ibm_users_num
