                recheck_period: $recheck_period | tonumber,
                version: $version
            }'
        )

        json=true
        jq_path='["default"]'
        JQ update "$CHANNELS_FILE" "$default"

        new_channels=()

        for((i=0;i<chnls_count;i++));
        do
