            iptv_source="$IPTV_ROOT"

            iptv_excludes_before=(
                /c/
                /ffmpeg-git-'*'
                /vip/
                node_modules/
            )

            ffmpeg_excludes_before=(
                /Amlogic_s905-kernel-master.zip
                /builds/
                /calibre/
                /dnscrypt/
                /ffmpeg_c
                /fontforge-20190413.tar.gz
                /imgcat.zip
                /jq-'*'
                /releases/
                /v2ray/
                /xray/
                '*'.ipk
                '*'.err
                '*'.log
                '*'.pid
            )

            if [[ "$FFMPEG_MIRROR_ROOT" =~ "$iptv_source"(.*) ]]
            then
                relative_path="${BASH_REMATCH[1]:-}"
                relative_path="${relative_path#/}"
                if [ -n "$relative_path" ] 
                then
                    for i in "${!ffmpeg_excludes_before[@]}"
                    do
                        if [[ "${ffmpeg_excludes_before[i]}" == /* ]] 
                        then
                            ffmpeg_excludes_before[i]="/${relative_path}${ffmpeg_excludes_before[i]}"
                        else
                            ffmpeg_excludes_before[i]="/$relative_path/${ffmpeg_excludes_before[i]}"
                        fi
                    done
                fi
                iptv_excludes_before+=("${ffmpeg_excludes_before[@]}")
            else
                ffmpeg_source="$FFMPEG_MIRROR_ROOT"
            fi

            if [[ "$LIVE_ROOT" =~ "$IPTV_ROOT"(.+) ]]
            then
                iptv_excludes_before+=("${BASH_REMATCH[1]}")
            fi

            node_excludes_before=(
                node_modules/
            )

            if ! [[ "$NODE_ROOT" =~ "$IPTV_ROOT"(.*) ]]
            then
                node_source="$NODE_ROOT"
            fi

            nginx_source=/usr/local/nginx

            nginx_excludes_before=(
                /logs/'*'.gz
                node_modules/
            )

            nginx_includes=(
                /conf/'***'
                /html/'***'
                /logs/'***'
            )

            nginx_excludes_after=(
                /'***'
            )

            openresty_source=/usr/local/openresty

            openresty_excludes_before=(
                /nginx/logs/'*'.gz
                node_modules/
            )

            openresty_includes=(
                /nginx/
                /nginx/conf/'***'
                /nginx/html/'***'
                /nginx/logs/'***'
                /nginx/lua/'***'
            )

            openresty_excludes_after=(
                /'***'
            )

            v2ray_source="${V2_CONFIG%/*}"

            xray_source="${X_CONFIG%/*}"

            DistCheck

            if [ "$dist" == "mac" ] 
            then
                systemd_source="$HOME"/Library/LaunchAgents
            else
                systemd_source=/etc/systemd/system
            fi

            systemd_includes=(
                /alist.service
                /aria2.service
                /dnscrypt-proxy.service
                /mmproxy-'*'.service
                /nginx.service
                /openresty.service
                /v2ray.service
                /xray.service
                /aios.'*'.service
                /com.aios.'*'.plist
            )

            systemd_excludes_after=(
                /'***'
            )

            home_source="$HOME"

            home_includes=(
                /.bash_history
            )

            home_excludes_after=(
                /'***'
            )

            DepInstall rsync

            mkdir -p "$BACKUP_ROOT"

            for backup in iptv nginx openresty v2ray xray ffmpeg node systemd home
            do
                rsync_commands=()
                source="${backup}_source"

                if [[ -z "${!source:-}" ]] 
                then
                    continue
                fi

                source="${!source}"

                if [ ! -d "$source" ] 
                then
                    continue
                fi

                excludes_before=("${backup}_excludes_before"[@])

                if [[ -n "${!excludes_before:-}" ]] 
                then
                    excludes_before=("${!excludes_before}")
                    for exclude in "${excludes_before[@]}"
                    do
                        rsync_commands+=( --exclude="$exclude" )
                    done
                fi

                includes=("${backup}_includes"[@])

                if [[ -n "${!includes:-}" ]] 
                then
                    includes_arr=("${!includes}")
                    for include in "${includes_arr[@]}"
                    do
                        rsync_commands+=( --include="$include" )
                    done
                fi

                excludes_after=("${backup}_excludes_after"[@])

                if [[ -n "${!excludes_after:-}" ]] 
                then
                    excludes_after=("${!excludes_after}")
                    for exclude in "${excludes_after[@]}"
                    do
                        rsync_commands+=( --exclude="$exclude" )
                    done
                fi

                rsync -avP --safe-links --delete --delete-excluded ${rsync_commands[@]+"${rsync_commands[@]}"} "$source/" "$BACKUP_ROOT/$backup/"
            done

            Println "$info 已创建备份 $BACKUP_ROOT\n"

            exit 0
        ;;
        *)
        ;;
    esac
fi

if [ -z "$*" ]
then
    ShFileCheck
    if [ "${vip:-false}" = true ] 
    then
        VipMenu
    else
        Menu
    fi
else
    while getopts "i:l:P:o:p:S:t:s:c:v:a:f:d:q:b:r:k:K:m:n:z:H:T:L:CReh" flag
    do
        case "$flag" in
            i) stream_link="$OPTARG";;
            l) live=false;;
            P) proxy="$OPTARG";;
            o) output_dir_name="$OPTARG";;
            p) playlist_name="$OPTARG";;
            S) seg_dir_name="$OPTARG";;
            t) seg_name="$OPTARG";;
            s) seg_length="$OPTARG";;
            c) seg_count="$OPTARG";;
            v) video_codec="$OPTARG";;
            a) audio_codec="$OPTARG";;
            f) video_audio_shift="$OPTARG";;
            d) txt_format="$OPTARG";;
            q) quality="$OPTARG";;
            b) bitrate="$OPTARG";;
            r) resolution="$OPTARG";;
            C) const=true;;
            R) const_cbr=true;;
            e) encrypt=true;;
            k) kind="$OPTARG";;
            K) key_name="$OPTARG";;
            m) input_flags="$OPTARG";;
            n) output_flags="$OPTARG";;
            z) channel_name="$OPTARG";;
            H) flv_h265=true;;
            T) flv_push_link="$OPTARG";;
            L) flv_pull_link="$OPTARG";;
            h|*) Usage;
        esac
    done

    if [ -z "${stream_link:-}" ]
    then
        Usage
    fi

    if [ ! -d "$IPTV_ROOT" ]
    then
        echo
        ExitOnList n "`gettext \"尚未安装, 是否现在安装\"`"
        Install
    else
        FFMPEG_ROOT=$(dirname "$IPTV_ROOT"/ffmpeg-git-*/ffmpeg)
        FFMPEG="$FFMPEG_ROOT/ffmpeg"
        FFPROBE="$FFMPEG_ROOT/ffprobe"

        GetDefault

        stream_links=("$stream_link")
        stream_link="${stream_links[0]}"

        if [ -z "${proxy:-}" ] 
        then
            if [[ $stream_link =~ ^https?:// ]] && [ -n "$d_proxy" ] 
            then
                echo
                inquirer list_input "`eval_gettext \"是否使用代理 \\\$d_proxy: \"`" yn_options use_proxy_yn
                if [ "$use_proxy_yn" == "$i18n_yes" ]
                then
                    proxy="$d_proxy"
                else
                    proxy=""
                fi
            else
                proxy=""
            fi
        fi

        if [ -z "${xc_proxy:-}" ] 
        then
            if [ -n "$d_xc_proxy" ] 
            then
                if [[ $stream_link =~ ^http://([^/]+) ]] 
                then
                    XtreamCodesGetDomains

                    xc_proxy=""
                    for xc_domain in "${xtream_codes_domains[@]}"
                    do
                        if [ "$xc_domain" == "${BASH_REMATCH[1]}" ] 
                        then
                            echo
                            inquirer list_input "`eval_gettext \"是否使用 xtream codes 代理 \\\$d_xc_proxy: \"`" yn_options use_proxy_yn
                            if [ "$use_proxy_yn" == "$i18n_yes" ]
                            then
                                xc_proxy="$d_xc_proxy"
                            else
                                xc_proxy=""
                            fi
                            break
                        fi
                    done
                else
                    xc_proxy=""
                fi
            else
                xc_proxy=""
            fi
        fi

        user_agent="$d_user_agent"
        headers="$d_headers"

        while [[ $headers =~ \\\\ ]]
        do
            headers=${headers//\\\\/\\}
        done

        if [ -n "$headers" ] && [[ ! $headers =~ \\r\\n$ ]]
        then
            headers="$headers\r\n"
        fi

        cookies="$d_cookies"
        output_dir_name="${output_dir_name:-$(RandOutputDirName)}"
        output_dir_root="$LIVE_ROOT/$output_dir_name"
        playlist_name="${playlist_name:-$(RandPlaylistName)}"
        seg_dir_name="${seg_dir_name:-$d_seg_dir_name}"

        if [ -n "$seg_dir_name" ] 
        then
            seg_dir_path="$seg_dir_name/"
        else
            seg_dir_path=""
        fi

        seg_name="${seg_name:-$playlist_name}"
        seg_length="${seg_length:-$d_seg_length}"
        seg_count="${seg_count:-$d_seg_count}"
        audio_codec="${audio_codec:-$d_audio_codec}"
        video_codec="${video_codec:-$d_video_codec}"
        use_primary_playlist=false
        hboasia_host="hbogoasia.com:8443"

        const=${const:-$d_const}
        const_cbr=${const_cbr:-$d_const_cbr}

        live=${live:-true}

        video_audio_shift="${video_audio_shift:-}"
        v_or_a="${video_audio_shift%_*}"

        if [ "$v_or_a" == "v" ] 
        then
            video_shift="${video_audio_shift#*_}"
        elif [ "$v_or_a" == "a" ] 
        then
            audio_shift="${video_audio_shift#*_}"
        fi

        txt_format="${txt_format:-$d_txt_format}"
        draw_text="${draw_text:-$d_draw_text}"
        quality="${quality:-$d_quality}"
        bitrate="${bitrate:-$d_bitrate}"
        resolution="${resolution:-$d_resolution}"

        if [ -n "$txt_format" ]
        then
            subtitle_append=',SUBTITLES="subs"'
        else
            subtitle_append=""
        fi

        master=0
        if [[ $bitrate =~ , ]] || [[ $quality =~ , ]] || [[ $resolution =~ , ]] || [ -n "$subtitle_append" ]
        then
            master=1
        fi

        if [ -z "${encrypt:-}" ]  
        then
            if [ "$d_encrypt" = true ] 
            then
                encrypt=true
                encrypt_session="$d_encrypt_session"
            else
                encrypt=false
                encrypt_session=false
            fi
        else
            encrypt=true
            encrypt_session="$d_encrypt_session"
        fi

        keyinfo_name="${keyinfo_name:-$d_keyinfo_name}"
        keyinfo_name="${keyinfo_name:-$(RandStr)}"
        key_name="${key_name:-$d_key_name}"
        key_name="${key_name:-$(RandStr)}"

        if [ "${stream_link:0:4}" == "rtmp" ] || [ "${stream_link:0:1}" == "/" ]
        then
            d_input_flags="${d_input_flags//-timeout 2000000000/}"
            d_input_flags="${d_input_flags//-reconnect 1/}"
            d_input_flags="${d_input_flags//-reconnect_at_eof 1/}"
            d_input_flags="${d_input_flags//-reconnect_streamed 1/}"
            d_input_flags="${d_input_flags//-reconnect_delay_max 2000/}"
            lead="${d_input_flags%%[^[:blank:]]*}"
            d_input_flags="${d_input_flags#${lead}}"
        elif [[ $stream_link == *".m3u8"* ]]
        then
            d_input_flags="${d_input_flags//-reconnect_at_eof 1/}"
        fi

        input_flags="${input_flags:-$d_input_flags}"
        if [[ ${input_flags:0:1} == "'" ]] 
        then
            input_flags="${input_flags%\'}"
            input_flags="${input_flags#\'}"
        fi

        if [ "${output_flags:-}" == "omit" ] 
        then
            output_flags=""
        else
            output_flags="${output_flags:-$d_output_flags}"
        fi

        if [[ ${output_flags:0:1} == "'" ]] 
        then
            output_flags="${output_flags%\'}"
            output_flags="${output_flags#\'}"
        fi

        channel_name="${channel_name:-$playlist_name}"
        sync="$d_sync"
        sync_file="${sync_file:-}"
        sync_index="${sync_index:-}"
        sync_pairs="${sync_pairs:-}"
        hls_end_list="${d_hls_end_list:-false}"

        [ ! -e $FFMPEG_LOG_ROOT ] && mkdir $FFMPEG_LOG_ROOT

        flv_h265="${flv_h265:-false}"
        flv_push_link="${flv_push_link:-}"
        flv_pull_link="${flv_pull_link:-}"

        extra_filters=""
        if [ "$video_codec" != "copy" ] && [ -n "$draw_text" ] 
        then
            filters=( vf filter:v )
            for filter in "${filters[@]}"
            do
                if [[ $output_flags =~ (.*)"-$filter "([^ ]+)(.*) ]] 
                then
                    extra_filters="${BASH_REMATCH[2]},"
                    output_flags="${BASH_REMATCH[1]} ${BASH_REMATCH[3]}"
                fi
            done
        fi

        flags_command=( -flags )
        if [[ $output_flags =~ (.*)"-flags "([^ ]+)(.*) ]] 
        then
            flags="${BASH_REMATCH[2]}"

            if [[ $flags =~ global_header ]] 
            then
                flags_command=( -flags "$flags" )
            else
                flags_command+=("-global_header$flags")
            fi

            output_flags="${BASH_REMATCH[1]} ${BASH_REMATCH[3]}"
        else
            flags_command+=(-global_header)
        fi

        FilterString input_flags output_flags

        from="AddChannel"

        if [ -n "${kind:-}" ] 
        then
            if [ "$kind" == "flv" ] 
            then
                if [ -z "${flv_push_link:-}" ] 
                then
                    Println "`eval_gettext \"\\\$error 未设置推流地址...\"`\n" && exit 1
                else
                    if [ "$sh_debug" -eq 1 ] 
                    then
                        ( FlvStreamCreator ) 
                    else
                        ( FlvStreamCreator ) > /dev/null 2> /dev/null < /dev/null &
                    fi
                fi
            else
                Println "`eval_gettext \"\\\$error 暂不支持输出 \$kind ...\"`\n" && exit 1
            fi
        else
            if [ "$sh_debug" -eq 1 ] 
            then
                ( HlsStreamCreatorPlus ) 
            else
                ( HlsStreamCreatorPlus ) > /dev/null 2> /dev/null < /dev/null &
            fi
        fi

        Println "`eval_gettext \"\\\$info 添加频道成功\"`\n"
    fi
fi
